$(function () {
    $("html").niceScroll();
    $(".down-arrow").on("click" , function () {
        $("html , body").animate({
            scrollTop: $("#about").offset().top
        } , 1000);
    })

    //navbar links
    $(".navbar li").on("click" , function () {
        let link = $(this).attr("data-value");
        $("html , body").animate({
            scrollTop: $("#" + link).offset().top
        },1000)
    })

    $(".fa-bars").on("click" , function () {
        $(".nav").slideToggle()
    })
})